'use client';

import {
  composeRenderProps,
  Tooltip as AriaTooltip,
  type TooltipProps as AriaTooltipProps,
  TooltipTrigger as AriaTooltipTrigger,
} from 'react-aria-components';

import { cn } from '~/lib/utils';

/**
 * TooltipTrigger component is used to trigger a tooltip.
 */
const TooltipTrigger = AriaTooltipTrigger;

/**
 * Renders a tooltip component.
 *
 * @component
 * @param {AriaTooltipProps} props - The props for the tooltip component.
 * @param {string} [props.className] - The class name for the tooltip component.
 * @param {number} [props.offset=4] - The offset for the tooltip component.
 * @returns {JSX.Element} The rendered tooltip component.
 */
function Tooltip({
  className,
  offset = 4,
  ...props
}: AriaTooltipProps): JSX.Element {
  return (
    <AriaTooltip
      className={composeRenderProps(className, (className) =>
        cn(
          'z-50 overflow-hidden rounded-md bg-primary px-3 py-1.5 text-xs text-primary-foreground animate-in fade-in-0',
          /* Entering */
          'data-[entering]:zoom-in-95',
          /* Exiting */
          'data-[exiting]:animate-out data-[exiting]:fade-out-0 data-[exiting]:zoom-out-95',
          /* Placement */
          'data-[placement=bottom]:slide-in-from-top-2 data-[placement=left]:slide-in-from-right-2 data-[placement=right]:slide-in-from-left-2 data-[placement=top]:slide-in-from-bottom-2',
          className,
        ),
      )}
      offset={offset}
      {...props}
    />
  );
}

export { Tooltip, TooltipTrigger };
